ember riders only(i.e start_station_name To end_station_name)\nmember_only <- member_only %>%\n  mutate(route = paste(start_station_name, \"To\", sep=\" \"))\n         \n         \nmember_only<- member_only %>%       \n  mutate(route = paste(route, end_station_name, sep =\" \"))\n\nhead(member_only, 10)","metadata":{"execution":{"iopub.status.busy":"2022-04-28T19:34:39.997926Z","iopub.execute_input":"2022-04-28T19:34:39.999527Z","iopub.status.idle":"2022-04-28T19:34:42.26031Z"},"trusted":true},"execution_count":null,"outputs":[]},{"cell_type":"code","source":"#To get the summary of member riders by route, number of rides, average duration.\nmember_top_route <- member_only %>%\n    group_by(route)%>%\n    summarize(number_of_rides = n(), average_duration = mean(ride_duration))\n    \nhead(member_top_route, 10)","metadata":{"execution":{"iopub.status.busy":"2022-04-28T19:35:06.419389Z","iopub.execute_input":"2022-04-28T19:35:06.421012Z","iopub.status.idle":"2022-04-28T19:35:10.531318Z"},"trusted":true},"execution_count":null,"outputs":[]},{"cell_type":"code","source":"#Creating the route column for casual riders only(i.e start_station_name To end_station_name)\ncasual_only <- casual_only %>%\n  mutate(route = paste(start_station_name, \"To\", sep=\" \"))\n         \n         \ncasual_only<- casual_only %>%       \n  mutate(route = paste(route, end_station_name, sep =\" \"))\n\nhead(casual_only, 10)","metadata":{"execution":{"iopub.status.busy":"2022-04-28T19:35:15.997576Z","iopub.execute_input":"2022-04-28T19:35:15.999117Z","iopub.status.idle":"2022-04-28T19:35:19.058281Z"},"trusted":true},"execution_count":null,"outputs":[]},{"cell_type":"code","source":"#To get the route with the most number of rides for member riders\nmember_top_route<- member_only%>%\n  group_by(route) %>%\n  summarise(number_of_rides  = n(), average_duration_seconds = mean(ride_duration))%>%\n  arrange(route, number_of_rides, average_duration_seconds)\n\n\n","metadata":{"execution":{"iopub.status